package Utils;

public class Day56PrimitivesPlay {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * The typecasting of int
		 */
		int x = 10;
		System.out.println("Int");
		System.out.println("The byte cast is:" + (byte) x);
		System.out.println("The long cast of is:" + (long) x);
		System.out.println("The char cast is:" + (char) x);
		System.out.println("The short cast is:" + (short) x);
		System.out.println("The float cast is:" + (float) x);
		System.out.println("The double cast is:" + (double) x);
		System.out.println("The int cast of is:" + (float) x);

		/*
		 * The typecasting of float
		 */
		float x1 = 10.456f;
		System.out.println("Float");
		System.out.println("The byte cast is:" + (byte) x1);
		System.out.println("The long cast of is:" + (long) x1);
		System.out.println("The char cast is:" + (char) x1);
		System.out.println("The short cast is:" + (short) x1);
		System.out.println("The int cast is:" + (int) x1);
		System.out.println("The double cast is:" + (double) x1);
		System.out.println("The int cast of is:" + (float) x1);

		/*
		 * The typecasting of short
		 */
		short x2 = 11;
		System.out.println("Short");
		System.out.println("The byte cast is:" + (byte) x2);
		System.out.println("The long cast of is:" + (long) x2);
		System.out.println("The char cast is:" + (char) x2);
		System.out.println("The short cast is:" + (short) x2);
		System.out.println("The int cast is:" + (int) x2);
		System.out.println("The double cast is:" + (double) x2);
		System.out.println("The int cast of is:" + (float) x2);

		/*
		 * The typecasting of short
		 */
		long x3 = 11133456457667464l;
		System.out.println("Long");
		System.out.println("The byte cast is:" + (byte) x3);
		System.out.println("The long cast of is:" + (long) x3);
		System.out.println("The char cast is:" + (char) x3);
		System.out.println("The short cast is:" + (short) x3);
		System.out.println("The int cast is:" + (int) x3);
		System.out.println("The double cast is:" + (double) x3);
		System.out.println("The int cast of is:" + (float) x3);

		/*
		 * The typecasting of double
		 */
		double x4 = 1113345.6457667464;
		System.out.println("Long");
		System.out.println("The byte cast is:" + (byte) x4);
		System.out.println("The long cast of is:" + (long) x4);
		System.out.println("The char cast is:" + (char) x4);
		System.out.println("The short cast is:" + (short) x4);
		System.out.println("The int cast is:" + (int) x4);
		System.out.println("The double cast is:" + (double) x4);
		System.out.println("The int cast of is:" + (float) x4);

		int a = 10;
		short b = (short) a;
		System.out.println("Casting int as short" + b);
		short c = 12;
		int d = (int) c;
		System.out.println("Casting short as int" + d);
		float y = 12.200f;
		long y1 = (long) y;
		System.out.println("Casting float as long" + y1);
		double y3 = 12.20022323312;
		long y4 = (long) y3;
		System.out.println("Casting double as long" + y4);
		short y5 = 2;
		long y6 = (long) y5;
		System.out.println("Casting short to long" + y6);

	}

}
